import React, {useState, useEffect, useParams } from 'react';
import {Document, Page,pdfjs } from 'react-pdf';
import 'react-pdf/dist/Page/AnnotationLayer.css';
import 'react-pdf/dist/Page/TextLayer.css';

pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;

function LectionPage() {
  const [links, setLinks] = useState([]);
  const id = useParams();
  const i = id;

  useEffect(()=>{
    fetch('http://localhost:3001/data')
    .then(response => response.json())
    .then((data)=>{
      console.log(data[0].lecture[i].link);
      setLinks(data[0].lecture[i].link);
    })
  }, [])

  return (
    <div className="lection__contents">
      <div className="menu"></div>
      <Document file={links} className={'lection__document'}>
        <Page pageNumber={1} renderTextLayer={false}></Page>
        <Page pageNumber={2} renderTextLayer={false}></Page>
        <Page pageNumber={3} renderTextLayer={false}></Page>
        <Page pageNumber={4} renderTextLayer={false}></Page>
        <Page pageNumber={5} renderTextLayer={false}></Page>
      </Document>
    </div>
  );
}
export default LectionPage;
