import React from "react";
import Right from "../img/icon/right.svg"
import { Link } from "react-router-dom";
import { useRef } from 'react';

function Index() {
    const sliderReff = useRef();
    function SlideRight() {
        sliderReff.current.scrollBy({
            left: 100,
        });
    }

    function SlideLeft() {
        sliderRef.current.scrollBy({
            left: -100,
        });
    }

    return (
        <div className="main">
            <div className="slider" ref={sliderReff}>
                <div className="slide b">
                    <button className="slider__btn" onClick={SlideRight}>
                        <img className="icon" src={Right} />
                    </button>
                    <Link to="/lection">
                        <div className="slide__element">Lection</div>
                    </Link>
                </div>
                <div className="slide r">
                    <button className="slider__btn" onClick={SlideLeft}>
                        <img className="icon" src={Right} />
                    </button>
                    <Link to="/practice">
                        <div className="slide__element">Practice</div>
                    </Link>
                </div>
            </div>
        </div>
    );
}

export default Index;
